formulas.expression:
IF (
  OR (
    TEXT({!$Record.ApplicationSource__c}) = 'ECO',
    TEXT({!$Record.ApplicationSource__c} )= 'PERSO'
  ),

  /* Perso order number must be on 7 digits */
  'P' + LPAD({!$Record.TECH_OrderNumber__c}, 7, '0')
   + '-' + 

  IF (NOT({!$Record.SPO_DisplayOrder__c}),
    {!$Record.Account.LastName},
    {!$Record.SPO_StoreCode__c} 
  ),
  {!$Record.Name}
)

IF (
    TEXT({!$Record.ApplicationSource__c}) = 'PERSO' 
    || TEXT({!$Record.ApplicationSource__c}) = 'ECO',
  /* Perso order number must be on 7 digits */
  'P' + LPAD({!$Record.TECH_OrderNumber__c}, 7, '0'), 
 /* SPO is on 6 digits and TECH_OrderNumber__c is already on 6 digits*/
/*UPDATE WS: SPO number is changed from SP000000 to S0000000 for MIY1765*/
  'S' +  LPAD({!$Record.TECH_OrderNumber__c}, 7, '0')
)
