value.stringValue:
IF ( OR ( TEXT([Opportunity].ApplicationSource__c) = 'ECO', TEXT([Opportunity].ApplicationSource__c )= 'PERSO' ),
/* Perso order number must be on 7 digits */ 'P' + LPAD([Opportunity].TECH_OrderNumber__c, 7, '0') + '-' +
IF (NOT([Opportunity].SPO_DisplayOrder__c), [Opportunity].Account.LastName, [Opportunity].SPO_StoreCode__c ), [Opportunity].Name )

formulas.expression:
IF (
  OR (
    TEXT({!myVariable_current.ApplicationSource__c}) = 'ECO',
    TEXT({!myVariable_current.ApplicationSource__c} )= 'PERSO'
  ),

  /* Perso order number must be on 7 digits */
  'P' + LPAD({!myVariable_current.TECH_OrderNumber__c}, 7, '0')
   + '-' + 

  IF (NOT({!myVariable_current.SPO_DisplayOrder__c}),
    {!myVariable_current.Account.LastName},
    {!myVariable_current.SPO_StoreCode__c} 
  ),
  {!myVariable_current.Name}
)

value.stringValue:
IF (
    TEXT([Opportunity].ApplicationSource__c) = 'PERSO' 
    || TEXT([Opportunity].ApplicationSource__c) = 'ECO',
  /* Perso order number must be on 7 digits */
  'P' + LPAD([Opportunity].TECH_OrderNumber__c, 7, '0'), 
 /* SPO is on 6 digits and TECH_OrderNumber__c is already on 6 digits*/
/*UPDATE WS: SPO number is changed from SP000000 to S0000000 for MIY1765*/
  'S' +  LPAD([Opportunity].TECH_OrderNumber__c, 7, '0')
)

formulas.expression:
IF (
    TEXT({!myVariable_current.ApplicationSource__c}) = 'PERSO' 
    || TEXT({!myVariable_current.ApplicationSource__c}) = 'ECO',
  /* Perso order number must be on 7 digits */
  'P' + LPAD({!myVariable_current.TECH_OrderNumber__c}, 7, '0'), 
 /* SPO is on 6 digits and TECH_OrderNumber__c is already on 6 digits*/
