  IF (
    HOUR(TIMEVALUE("00:00:00.000") + CallDurationInSeconds*1000) < 10,
    '0',
    ''
  ) +
  TEXT(HOUR(TIMEVALUE("00:00:00.000") + CallDurationInSeconds*1000)) + ':' +

  /* Minute */
  IF (
    MINUTE(TIMEVALUE("00:00:00.000") + CallDurationInSeconds*1000) < 10,
    '0',
    ''
  ) +
  TEXT(MINUTE(TIMEVALUE("00:00:00.000") + CallDurationInSeconds*1000)) + ':' +

  /* Second*/
  IF (
    SECOND(TIMEVALUE("00:00:00.000") + CallDurationInSeconds*1000) < 10,
    '0',
    ''
  ) +
  TEXT(SECOND(TIMEVALUE("00:00:00.000") + CallDurationInSeconds*1000)),

  ''
