ValidationRule.errorConditionFormula:
AND (
	NOT( $Permission.Corps_Access ), 
	OR(

		AND ( 	
			ISPICKVAL($User.Country__c,'KOREA') , 
 			NOT(
				ISPICKVAL(Store__r.StoreCountry__c, 'KOREA')
			)
		),

		AND ( 
			ISPICKVAL( $User.MANAGEMENT_ZONE_LEVEL__c , '13'), 

			NOT(ISPICKVAL($User.Country__c,'KOREA')), 

			ISPICKVAL(  Store__r.StoreCountry__c  , 'KOREA')

		),

		NOT(
			TEXT( $User.MANAGEMENT_ZONE_LEVEL__c) == TEXT( Store__r.MANAGEMENT_ZONE_LEVEL__c) 
		)
	)
)
